package week31029;

public class Swap {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int first = 3;
		int second = 4;
		System.out.println("First variable: "+first+" Second variable: "+second);
		first = first + second;
		second = first - second;
		first = first - second;
		System.out.println("First variable: "+first+" Second variable: " + second);
		
		
	}

}


